global.css.content:
body {
    font-family: Arial, sans-serif;
    margin: 0;
    padding: 0;
    background-color: #f8f9fa;
}

.sidebar {
    width: 250px;
    min-height: 100vh;
}

.music-player {
    position: fixed;
    bottom: 0;
    width: 100%;
    height: 70px;
    border-top: 1px solid #444;
    display: flex;
    justify-content: space-between;
    z-index: 1000;
}

.album-cover {
    border-radius: 5px;
    object-fit: cover;
}

.player-controls button {
    font-size: 18px;
}

#track-title {
    font-weight: bold;
}

#artist-name {
    font-size: 12px;
}

player.js.content:
const audioPlayer = document.getElementById('audio-player');
const playPauseBtn = document.getElementById('play-pause-btn');
const playIcon = document.getElementById('play-icon');
const prevBtn = document.getElementById('prev-btn');
const nextBtn = document.getElementById('next-btn');
const trackTitle = document.getElementById('track-title');
const artistName = document.getElementById('artist-name');

// Example track list
const tracks = [
    { src: 'https://vtippiwsuhcvidotouio.supabase.co/storage/v1/object/public/music/360TrickShot%20-%20A%20COLORS%20SHOW.mp3', title: '360 TrickShot', artist: 'Jolagreen123' },
    { src: '/public/music/song2.mp3', title: 'Chanson 2', artist: 'Artiste 2' },
    { src: '/public/music/song3.mp3', title: 'Chanson 3', artist: 'Artiste 3' },
];

let currentTrackIndex = 0;

// Load a track
function loadTrack(index) {
    const track = tracks[index];
    audioPlayer.src = track.src;
    trackTitle.textContent = track.title;
    artistName.textContent = track.artist;
    audioPlayer.load();
    savePlayerState();
}

// Save the current state of the player
function savePlayerState() {
    const playerState = {
        currentTrackIndex,
        currentTime: audioPlayer.currentTime,
        isPlaying: !audioPlayer.paused,
    };
    localStorage.setItem('playerState', JSON.stringify(playerState));
}

// Restore the saved state of the player
function restorePlayerState() {
    const savedState = localStorage.getItem('playerState');
    if (savedState) {
        const { currentTrackIndex: savedIndex, currentTime, isPlaying } = JSON.parse(savedState);
        currentTrackIndex = savedIndex || 0;
        loadTrack(currentTrackIndex);
        audioPlayer.currentTime = currentTime || 0;
        if (isPlaying) {
            audioPlayer.play();
            playIcon.className = 'bi bi-pause-fill';
        }
    } else {
        loadTrack(currentTrackIndex); // Default to the first track
    }
}

// Play or Pause
playPauseBtn.addEventListener('click', () => {
    if (audioPlayer.paused) {
        audioPlayer.play();
        playIcon.className = 'bi bi-pause-fill';
    } else {
        audioPlayer.pause();
        playIcon.className = 'bi bi-play-fill';
    }
    savePlayerState();
});

// Next Track
nextBtn.addEventListener('click', () => {
    currentTrackIndex = (currentTrackIndex + 1) % tracks.length;
    loadTrack(currentTrackIndex);
    audioPlayer.play();
    playIcon.className = 'bi bi-pause-fill';
    savePlayerState();
});

// Previous Track
prevBtn.addEventListener('click', () => {
    currentTrackIndex = (currentTrackIndex - 1 + tracks.length) % tracks.length;
    loadTrack(currentTrackIndex);
    audioPlayer.play();
    playIcon.className = 'bi bi-pause-fill';
    savePlayerState();
});

// Update state when audio ends
audioPlayer.addEventListener('ended', () => {
    nextBtn.click();
});

// Save state when time updates
audioPlayer.addEventListener('timeupdate', savePlayerState);

// Initialize
restorePlayerState();
